jQuery(document).ready(function(e){
    function t(){
        e(".sf-menu").superfish({
            delay: 50,
            autoArrows: !0,
            animation: { opacity: "show" }
        });
        e("nav > ul.sf-menu > li").each(function(){
            e(this).find(".sf-with-ul").append('<i class="fa fa-angle-down"></i>')
        });
    }

    function a(){
        e("#portfolio-carousel").owlCarousel({
            items: 4,
            navigation: !1,
            pagination: !1,
            autoPlay: !0,
            navigationText: ["",""]
        });
    }

    function i(){
        e(".bxslider").bxSlider({
            adaptiveHeight: !0,
            controls: !1,
            auto: !0,
            mode: "fade"
        });
    }

    function n(){
        e(".flexslider").flexslider({
            controlNav: !1,
            animation: "slide",
            prevText: "",
            nextText: ""
        });
    }

    function o(){
        e(".fancybox").fancybox({
            padding: 5,
            titlePosition: "over"
        });
    }

    function s(){
        e("#flickr-feed").jflickrfeed({
            limit: 8,
            qstrings: { id: "44802888@N04" },
            itemTemplate: '<li><a href="{{image_b}}" class="fancybox"><img src="{{image_s}}" alt="{{title}}" /></a></li>'
        });
    }

    // Responsieve menu en overige elementen
    e(window).resize(function(){
        e(window).width() >= 768 && e(".responsive_menu").hide()
    });

    t();
    a();
    i();
    n();
    o();
    e("#Grid").mixitup({
        effects: ["fade", "grayscale"],
        easing: "snap",
        transitionSpeed: 400
    });
    s();

    // Parallax elementen
    e("#homeIntro").parallax("50%", 0.3);
    e("#blogPosts").parallax("80%", 0.3);
    e(".pageTitle").parallax("50%", 0.3);

    // Mobile menu toggle
    e(".menu-toggle-btn").click(function(){
        e(".responsive_menu").slideToggle();
    });
});
